def soup(matrix, word, col = None, line = None): return len(word) == 0 or (matrix[line][col] == word[0] and any([soup(matrix, word[1:], col + dx, line + dy) for dx in [-1, 0, 1] for dy in [-1, 0, 1] if 0 <= col + dx < len(matrix[0]) and 0 <= line + dy < len(matrix)])) if col is not None and line is not None else "".join([f"{chr(ord('A') + line)}{col + 1}" for line, col in sorted([(line, col) for col in range(len(matrix[0])) for line in range(len(matrix)) if soup(matrix, word, col, line)])])[:2] # "os one-liners do Miguel" :(
